allow:
Bash(powershell -ExecutionPolicy Bypass -File - <<'PS1'
$notifyPath = Join-Path $env:USERPROFILE '.claude' 'claude-notify.ps1'
$tabActivePath = Join-Path $env:USERPROFILE '.claude' 'claude-tab-active.ps1'

Write-Host "=== claude-notify.ps1 encoding ==="
$bytes = [System.IO.File]::ReadAllBytes\($notifyPath\)
# Check BOM
if \($bytes[0] -eq 0xEF -and $bytes[1] -eq 0xBB -and $bytes[2] -eq 0xBF\) {
    Write-Host "BOM: UTF-8 with BOM"
} elseif \($bytes[0] -eq 0xFF -and $bytes[1] -eq 0xFE\) {
    Write-Host "BOM: UTF-16 LE"
} else {
    Write-Host "BOM: None \(no BOM detected\)"
    Write-Host "First 3 bytes: $\($bytes[0]\) $\($bytes[1]\) $\($bytes[2]\)"
}

Write-Host "`n=== Searching for emoji bytes in claude-notify.ps1 ==="
$content = [System.IO.File]::ReadAllText\($notifyPath, [System.Text.Encoding]::UTF8\)
$lines = $content -split "`n"
for \($i = 0; $i -lt $lines.Count; $i++\) {
    if \($lines[$i] -match 'Title'\) {
        Write-Host "Line $\($i+1\): $\($lines[$i].Trim\(\)\)"
    }
}

Write-Host "`n=== claude-tab-active.ps1 emoji lines ==="
$content2 = [System.IO.File]::ReadAllText\($tabActivePath, [System.Text.Encoding]::UTF8\)
$lines2 = $content2 -split "`n"
for \($i = 0; $i -lt $lines2.Count; $i++\) {
    if \($lines2[$i] -match 'Title'\) {
        Write-Host "Line $\($i+1\): $\($lines2[$i].Trim\(\)\)"
    }
}

Write-Host "`n=== Windows Terminal suppressApplicationTitle ==="
$wtSettingsPath = Join-Path $env:LOCALAPPDATA 'Packages\\Microsoft.WindowsTerminal_8wekyb3d8bbwe\\LocalState\\settings.json'
if \(Test-Path $wtSettingsPath\) {
    $wtSettings = Get-Content $wtSettingsPath -Raw | ConvertFrom-Json
    Write-Host "suppressApplicationTitle in defaults: $\($wtSettings.profiles.defaults.suppressApplicationTitle\)"
} else {
    Write-Host "Windows Terminal settings not found at standard path"
    # Try non-store version
    $altPath = Join-Path $env:LOCALAPPDATA 'Microsoft\\Windows Terminal\\settings.json'
    if \(Test-Path $altPath\) {
        Write-Host "Found at: $altPath"
        $wtSettings = Get-Content $altPath -Raw | ConvertFrom-Json
        Write-Host "suppressApplicationTitle in defaults: $\($wtSettings.profiles.defaults.suppressApplicationTitle\)"
    } else {
        Write-Host "Also not found at: $altPath"
    }
}
PS1)
